'use strict';
import Bluebird from 'bluebird';

/**
 * accessor class for authorization over a {@link BouncerToken}
 */
class Authorization {
  /**
   *@param {BouncerToken} token - the underlying bouncer token
   */
  constructor(token) {
    this._token = token;
  }

  /**
   * set and save a value against the bouncer token
   * @param {string} key - the key to save against
   * @param {object} value - the value to save
   * @param {function(auth:Authorization)} [callback] - a callback to call after data is saved
   * @returns {Promise<Authorization} - return the authorization once data has been saved
   */
  set(key, value, callback) {
    this._token.state[key] = value;
    this._token.markModified('state');
    return Bluebird.resolve(
        this._token.saveAsync()
        .then(() => {
          return this;
        }))
      .nodeify(callback);
  }

  /**
   * get a value associated with a key
   * @param {String} key - the key that the value is saved against
   * @returns {object}
   */
  get(key) {
    return this._token.state[key];
  }

  /**
   * delete a key in a bouncer token
   * @param {string} key - the key to save against
   * @param {function(auth:Authorization)} [callback] - a callback to call after data is deleted
   * @returns {Promise<Authorization} - return the authorization once data has been deleted
   */
  delete(key, callback) {
    delete this._token.state[key];
    this._token.markModified('state');
    return Bluebird.resolve(
        this._token.saveAsync()
        .then(() => {
          return this;
        }))
      .nodeify(callback);
  }
}

export default Authorization;

'use strict';
import ConnectorProxy from './proxy';
import Context from '@hoist/context';
import {
  ConnectorSetting
}
from '@hoist/model';
import Errors from '@hoist/errors';
import logger from '@hoist/logger';
import Authorization from './Authorization';
/**
 * Pipeline object for interaction with connectors
 */
class ConnectorPipeline {
  /**
   * create a new ConnectorPipeline object
   */
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
  }
  _loadSettings(key, context) {
      return ConnectorSetting.findOneAsync({
          key: key,
          environment: context.environment || 'live',
          application: context.application._id
        })
        .then((settings) => {
          if (!settings) {
            throw new Errors.connector.request.InvalidError('no settings found with the key ' + key);
          }
          if (context.application && context.application.runscope && context.application.runscope.bucket) {
            settings.settings.runscopeBucket = context.application.runscope.bucket;
          }
          return settings;
        });
    }
    /**
     * load the connector identified by the key
     * @param {string} key - the unique key identifer for the connector
     * @returns {Promise<ConnectorProxy>} - a connector proxy
     */
  loadConnector(key) {
    return Promise.resolve().then(() => {
      return Context.get()
        .then((context) => {
          return this._loadSettings(key, context)
            .then((settings) => {
              var proxy = new ConnectorProxy(settings);
              return proxy.init(context).then(() => {
                return proxy;
              });
            });
        });

    });
  }
}
export {
  Authorization as Authorization
};

export default ConnectorPipeline;

'use strict';
var config = require('config');
var path = require('path');
var errors = require('@hoist/errors');
var Authorization = require('./authorization');
var fs = require('fs');
import {
  filter, functions
}
from 'lodash';
import {
  BouncerToken
}
from '@hoist/model';
import logger from '@hoist/logger';


/**
 * a proxy class to the underlying connector
 */
class ConnectorProxy {
  /**
   * setup the connector proxy described by the {@link ConnectorSetting} object
   * @param {ConnectorSetting} connectorSetting - the settings for the underlying connector
   */
  constructor(connectorSetting) {
    this._logger = logger.child({
      cls: this.constructor.name,
      applicationId: connectorSetting.application,
      connectorKey: connectorSetting.key
    });
    this._connectorSetting = connectorSetting;
    let connectorsPath = path.resolve(config.get('Hoist.filePaths.connectors'));
    let connectorPath = path.join(connectorsPath, connectorSetting.connectorType, 'current');
    this._connectorPath = fs.realpathSync(connectorPath);
    this._settings = this._connectorSetting.settings;
  }

  /**
   * initialize this proxy instance
   * @param {Context} context - the current context
   * @returns {Promise<ConnectorProxy>} - this proxy object to allow chaining
   */
  init(context) {
    return Promise.resolve()
      .then(() => {
        let ConnectorType = require(this._connectorPath);
        this._connector = new ConnectorType(this._settings);
        let methods = filter(functions(this._connector), (property) => {
          if (property.startsWith('_') || property === 'receiveBounce' || this[property]) {
            return false;
          } else {
            return true;
          }
        });
        methods.forEach((method) => {
          /**
           * also has all methods of underlying connector
           */
          this[method] = (...params) => {
            this._logger.info('proxying method ' + method);
            if (typeof this._connector[method] !== 'function') {
              throw new errors.connector.request.UnsupportedError(method + ' method unsupported');
            }
            return this._connector[method].apply(this._connector, params);
          };
        });
        if (context.bucket &&
          context.bucket.meta &&
          context.bucket.meta.authToken &&
          context.bucket.meta.authToken[this._connectorSetting.key]) {
          return this.authorize(context.bucket.meta.authToken[this._connectorSetting.key])
            .catch((err) => {
              this._logger.error(err);
              if (err instanceof errors.connector.request.InvalidError) {
                return this;
              } else {
                throw err;
              }
            });
        } else {
          return this;
        }
      });
  }

  /**
   * authorize the underlying connector
   * @param {Authorization|String} token - either the authorization object or a bouncer token key
   * @returns {Promise<ConnectorProxy>} - this
   */
  authorize(token) {
    if (typeof this._connector.authorize !== 'function') {
      return Promise.resolve(this);
    }
    if (typeof token === 'string') {
      return BouncerToken.findOneAsync({
        key: token
      }).then((bouncerToken) => {
        if (!bouncerToken) {
          throw new errors.connector.request.InvalidError('the authorization token provided is not valid');
        }
        return this._connector.authorize(new Authorization(bouncerToken));
      }).then(() => {
        return this;
      });
    } else {
      return Promise.resolve(this._connector.authorize(token))
        .then(() => {
          return this;
        });
    }
  }
}

export default ConnectorProxy;

