'use strict';
import Bluebird from 'bluebird';

/**
 * accessor class for authorization over a {@link BouncerToken}
 */
class Authorization {
  /**
   *@param {BouncerToken} token - the underlying bouncer token
   */
  constructor(token) {
    this._token = token;
  }

  /**
   * set and save a value against the bouncer token
   * @param {string} key - the key to save against
   * @param {object} value - the value to save
   * @param {function(auth:Authorization)} [callback] - a callback to call after data is saved
   * @returns {Promise<Authorization} - return the authorization once data has been saved
   */
  set(key, value, callback) {
    this._token.state[key] = value;
    this._token.markModified('state');
    return Bluebird.resolve(
        this._token.saveAsync()
        .then(() => {
          return this;
        }))
      .nodeify(callback);
  }

  /**
   * get a value associated with a key
   * @param {String} key - the key that the value is saved against
   * @returns {object}
   */
  get(key) {
    return this._token.state[key];
  }

  /**
   * delete a key in a bouncer token
   * @param {string} key - the key to save against
   * @param {function(auth:Authorization)} [callback] - a callback to call after data is deleted
   * @returns {Promise<Authorization} - return the authorization once data has been deleted
   */
  delete(key, callback) {
    delete this._token.state[key];
    this._token.markModified('state');
    return Bluebird.resolve(
        this._token.saveAsync()
        .then(() => {
          return this;
        }))
      .nodeify(callback);
  }
}

export default Authorization;

'use strict';
import ConnectorProxy from './proxy';
import Context from '@hoist/context';
import {
  ConnectorSetting
}
from '@hoist/model';
import Errors from '@hoist/errors';
import logger from '@hoist/logger';
import Authorization from './authorization';
/**
 * Pipeline object for interaction with connectors
 */
class ConnectorPipeline {
  /**
   * create a new ConnectorPipeline object
   */
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
  }
  _loadSettings(key, context) {
    return ConnectorSetting.findOneAsync({
        key: key,
        environment: context.environment || 'live',
        application: context.application._id
      })
      .then((settings) => {
        if (!settings) {
          throw new Errors.connector.request.InvalidError('no settings found with the key ' + key);
        }
        settings.settings = settings.settings || {};
        settings.settings.applicationId = context.application.id;
        settings.settings.applicationName = context.application.name;
        if (context.application && context.application.runscope && context.application.runscope.bucket) {
          settings.settings.runscopeBucket = context.application.runscope.bucket;
        }
        return settings;
      });
  }

  /**
   * load the connector identified by the key
   * @param {string} key - the unique key identifer for the connector
   * @returns {Promise<ConnectorProxy>} - a connector proxy
   */
  loadConnector(key) {
    return Promise.resolve().then(() => {
      return Context.get()
        .then((context) => {
          return this._loadSettings(key, context)
            .then((settings) => {
              var proxy = new ConnectorProxy(settings);
              return proxy.init(context).then(() => {
                return proxy;
              });
            });
        });

    });
  }
}
export {
  Authorization as Authorization
};

